package com.luffy.paylib.payType.ali;import android.text.TextUtils;import java.util.Map;/** * Created by lvlufei on 2018/7/28 * * @desc 支付宝支付结果 */public class AliPayResult {    private String resultStatus;    private String result;    private String memo;    public AliPayResult(Map<String, String> rawResult) {        if (rawResult == null) {            return;        }        for (String key : rawResult.keySet()) {            if (TextUtils.equals(key, "resultStatus")) {                resultStatus = rawResult.get(key);            } else if (TextUtils.equals(key, "result")) {                result = rawResult.get(key);            } else if (TextUtils.equals(key, "memo")) {                memo = rawResult.get(key);            }        }    }    public String getResultStatus() {        return resultStatus;    }    public void setResultStatus(String resultStatus) {        this.resultStatus = resultStatus;    }    public String getResult() {        return result;    }    public void setResult(String result) {        this.result = result;    }    public String getMemo() {        return memo;    }    public void setMemo(String memo) {        this.memo = memo;    }}